#script version 4.0 - better credential detection and integrated authentication - commented for usability

#Ingest hostname and credentials (if present) from LogicMonitor
$hostname = "##SYSTEM.SYSNAME##";
$wmi_user = "##WMI.USER##";
$wmi_pass = "##WMI.PASS##";

#Are wmi user/ pass set? -- e.g. are these device properties blank - or is there something useful present?
if ( (($wmi_user -like '*WMI.USER*') -and ($wmi_pass -like '*WMI.PASS*')) -or (($wmi_user -eq '') -and ($wmi_pass -eq '')) )
{
    $use_credentials = $FALSE;
    $method = "Invoke-Command";
}

else
{
    # convert user/ password into a credential object
    $use_credentials = $TRUE;
    $method = "Import-PSSession";

    $remote_pass       = ConvertTo-SecureString -String $wmi_pass -AsPlainText -Force;
    $remote_credential = New-Object -typename System.Management.Automation.PSCredential -argumentlist $wmi_user, $remote_pass;
}

#If we found credentials above, we will use them
if ( $use_credentials ) {
    #establish a persistent session and import commands to the local collector
    $session = New-PSSession -ComputerName $hostname -authentication kerberos -Credential $remote_credential;
    Import-PSSession $session -CommandName Get-DhcpServerv4Scope -AllowClobber | Out-Null;

    #Retrieve the list of AD Groups on the server
    $leases = Get-DhcpServerv4Scope | Get-DhcpServerv4Lease | Format-Table

    $leases

    Write-host "Remote PowerShell via" $method

    Remove-PSSession $session

    exit
}
else {
    #try integrated authentication when we don't detect credentials
    $leases = Invoke-Command -ComputerName $hostname -ScriptBlock {
        Get-DhcpServerv4Scope | Get-DhcpServerv4Lease | Format-Table
    }

    $leases

    Write-host "Remote PowerShell via" $method

}

Exit
